import{d as e}from"./app.5176ced8.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const l={},a=e('<h1 id="\u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#\u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406" aria-hidden="true">#</a> \u6D4F\u89C8\u5668\u5DE5\u4F5C\u539F\u7406</h1><h2 id="\u6D4F\u89C8\u5668\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u6D4F\u89C8\u5668\u67B6\u6784" aria-hidden="true">#</a> \u6D4F\u89C8\u5668\u67B6\u6784</h2><p>\u6D4F\u89C8\u5668\u65E9\u671F\u90FD\u662F\u5355\u8FDB\u7A0B\u67B6\u6784, \u5BFC\u81F4\u7684\u95EE\u9898\u662F\u4E0D\u7A33\u5B9A, \u4E0D\u6D41\u7545, \u4E0D\u5B89\u5168, \u540E\u6765 chrome \u5F00\u542F\u4E86\u591A\u8FDB\u7A0B\u67B6\u6784\u65F6\u4EE3, \u4E2D\u95F4\u4E5F\u7ECF\u8FC7\u4E86\u591A\u6B21\u8FED\u4EE3, \u76EE\u524D\u662F\u9762\u5411\u670D\u52A1\u67B6\u6784, \u7531\u6D4F\u89C8\u5668\u4E3B\u8FDB\u7A0B, \u5404\u79CD\u670D\u52A1\u8FDB\u7A0B(GPU \u8FDB\u7A0B, \u7F51\u7EDC\u8FDB\u7A0B, \u6587\u4EF6\u8FDB\u7A0B\u7B49), \u6BCF\u4E2A\u9875\u9762\u5355\u72EC\u7684\u6E32\u67D3\u8FDB\u7A0B(\u5BF9\u4E8E\u540C\u4E00\u7AD9\u70B9, \u5373\u534F\u8BAE\u4E0E\u6839\u57DF\u540D\u76F8\u540C\u7684\u7AD9\u70B9, chrome \u4F1A\u590D\u7528\u6E32\u67D3\u8FDB\u7A0B), \u63D2\u4EF6\u8FDB\u7A0B, \u5171\u540C\u6784\u6210.</p><p>\u5404\u9875\u9762\u6E32\u67D3\u8FDB\u7A0B\u7684\u72EC\u7ACB\u4F7F\u5F97\u9875\u9762\u811A\u672C\u7684\u6267\u884C\u4E92\u4E0D\u5E72\u6270, \u89E3\u51B3\u4E86\u4E0D\u7A33\u5B9A\u4E0D\u6D41\u7545\u7684\u95EE\u9898, \u540C\u65F6\u5404\u4E2A\u8FDB\u7A0B\u662F\u5728\u6C99\u7BB1\u4E2D\u8FD0\u884C\u7684, \u4E5F\u66F4\u5B89\u5168\u4E86.</p><h2 id="\u6570\u636E\u4F20\u8F93" tabindex="-1"><a class="header-anchor" href="#\u6570\u636E\u4F20\u8F93" aria-hidden="true">#</a> \u6570\u636E\u4F20\u8F93</h2><p>\u7F51\u7EDC\u5C42\u7684 IP \u534F\u8BAE\u8D1F\u8D23\u628A\u6570\u636E\u5305\u9001\u5230\u76EE\u6807\u4E3B\u673A, \u4F20\u8F93\u5C42\u7684 TCP/UDP \u534F\u8BAE\u8D1F\u8D23\u628A\u6570\u636E\u5305\u9001\u5230\u76EE\u6807\u5E94\u7528\u7AEF\u53E3, \u5176\u4E2D TCP \u534F\u8BAE\u901A\u8FC7\u4E09\u6B21\u63E1\u624B, \u4F20\u8F93\u6570\u636E, \u56DB\u6B21\u6325\u624B\u4FDD\u8BC1\u4E86\u6570\u636E\u7A33\u5B9A\u6027.</p><p>\u6D4F\u89C8\u5668\u591A\u4E2A\u6807\u7B7E\u5747\u53D1\u8D77\u8BF7\u6C42\u65F6, \u6E90\u7AEF\u53E3\u662F\u76F8\u540C\u7684, \u4F46\u7F51\u7EDC\u8FDB\u7A0B\u77E5\u9053\u6BCF\u4E2A TCP \u8FDE\u63A5\u5BF9\u5E94\u7684\u6807\u7B7E, \u6240\u4EE5\u63A5\u6536\u5230\u6570\u636E\u540E, \u4F1A\u5C06\u5185\u5BB9\u5206\u53D1\u5230\u76EE\u6807\u6807\u7B7E\u9875\u7684\u6E32\u67D3\u8FDB\u7A0B.</p><h2 id="\u4ECE\u8F93\u5165-url-\u5230\u9875\u9762\u5C55\u793A-\u4E2D\u95F4\u53D1\u751F\u4E86\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u4ECE\u8F93\u5165-url-\u5230\u9875\u9762\u5C55\u793A-\u4E2D\u95F4\u53D1\u751F\u4E86\u4EC0\u4E48" aria-hidden="true">#</a> \u4ECE\u8F93\u5165 URL \u5230\u9875\u9762\u5C55\u793A, \u4E2D\u95F4\u53D1\u751F\u4E86\u4EC0\u4E48?</h2><p>\u8FD9\u4E2A\u95EE\u9898\u6211\u7528 chrome \u6D4F\u89C8\u5668\u7684\u884C\u4E3A\u6765\u63CF\u8FF0\u4E00\u4E0B.</p><ol><li><p>\u6D4F\u89C8\u5668\u5730\u5740\u680F\u5224\u65AD\u8F93\u5165\u7684\u5185\u5BB9\u662F\u5426\u7B26\u5408 URL \u89C4\u5219, \u5982\u679C\u4E0D\u7B26\u5408, \u4F1A\u4F7F\u7528\u9ED8\u8BA4\u7684\u641C\u7D22\u5F15\u64CE\u5408\u6210\u5E26\u641C\u7D22\u5173\u952E\u5B57\u7684 URL, \u5982\u679C\u7B26\u5408, \u4F1A\u52A0\u4E0A\u534F\u8BAE, \u628A\u5B83\u5408\u6210\u4E3A\u5B8C\u6574\u7684 URL, \u540C\u65F6\u5F53\u524D\u9875\u9762\u4E5F\u4F1A\u89E6\u53D1 beforeunload \u4E8B\u4EF6.</p></li><li><p>\u63A5\u4E0B\u6765\u5C31\u8FDB\u5165\u9875\u9762\u8D44\u6E90\u8BF7\u6C42\u9636\u6BB5, \u6D4F\u89C8\u5668\u8FDB\u7A0B\u4F1A\u901A\u8FC7 IPC \u5C06 URL \u8BF7\u6C42\u53D1\u9001\u81F3\u7F51\u7EDC\u8FDB\u7A0B\u4E2D, \u7531\u7F51\u7EDC\u8FDB\u7A0B\u53D1\u8D77\u771F\u6B63\u7684\u8BF7\u6C42. \u9996\u5148\u7F51\u7EDC\u8FDB\u7A0B\u4F1A\u67E5\u627E\u672C\u5730\u7F13\u5B58, \u5982\u679C\u627E\u5230\u4E86\u7F13\u5B58\u8D44\u6E90\u5C31\u76F4\u63A5\u8FD4\u56DE\u7ED9\u6D4F\u89C8\u5668\u8FDB\u7A0B, \u6CA1\u6709\u627E\u5230\u5219\u8FDB\u5165\u7F51\u7EDC\u8BF7\u6C42\u6D41\u7A0B.\u7F51\u7EDC\u8BF7\u6C42\u7684\u7B2C\u4E00\u6B65\u662F DNS \u89E3\u6790, \u83B7\u53D6\u670D\u52A1\u5668 IP \u5730\u5740, \u5982\u679C\u662F HTTPS \u534F\u8BAE, \u5219\u8FD8\u8981\u5EFA\u7ACB TLS \u8FDE\u63A5. \u63A5\u7740\u5C31\u662F\u5229\u7528 IP \u5730\u5740\u5EFA\u7ACB TCP \u8FDE\u63A5, \u5EFA\u7ACB\u5B8C\u6210\u540E\u6D4F\u89C8\u5668\u4F1A\u6784\u5EFA\u8BF7\u6C42\u884C\u548C\u8BF7\u6C42\u5934, \u5E76\u5E26\u4E0A\u548C\u8BE5\u57DF\u540D\u76F8\u5173\u7684 Cookie, \u53D1\u9001\u7ED9\u670D\u52A1\u5668. \u670D\u52A1\u5668\u63A5\u6536\u5230\u8BF7\u6C42\u6D88\u606F\u540E, \u751F\u6210\u54CD\u5E94\u6570\u636E\u53D1\u9001\u7ED9\u6D4F\u89C8\u5668\u7684\u7F51\u7EDC\u8FDB\u7A0B. \u7F51\u7EDC\u8FDB\u7A0B\u5728\u63A5\u6536\u5230\u54CD\u5E94\u884C\u548C\u54CD\u5E94\u5934\u540E, \u5C31\u5F00\u59CB\u89E3\u6790.</p></li></ol><p>\u5982\u679C\u8FD4\u56DE\u7684\u72B6\u6001\u7801\u662F 301 \u6216\u8005 302, \u4EE3\u8868\u670D\u52A1\u5668\u8981\u6D4F\u89C8\u5668\u91CD\u5B9A\u5411\u5230\u522B\u7684 URL, \u5219\u7F51\u7EDC\u8FDB\u7A0B\u4F1A\u8BFB\u53D6\u54CD\u5E94\u5934\u4E2D\u7684 Location \u5B57\u6BB5\u83B7\u53D6\u91CD\u5B9A\u5411\u7684\u5730\u5740, \u7136\u540E\u91CD\u65B0\u5F00\u59CB\u9875\u9762\u8D44\u6E90\u8BF7\u6C42.</p><p>\u5982\u679C\u8FD4\u56DE\u7684\u662F 200, \u6D4F\u89C8\u5668\u4F1A\u6839\u636E Content-Type \u5B57\u6BB5\u51B3\u5B9A\u5982\u4F55\u663E\u793A\u54CD\u5E94\u4F53\u7684\u5185\u5BB9, \u5047\u5982\u8BE5\u5B57\u6BB5\u88AB\u6D4F\u89C8\u5668\u5224\u65AD\u4E3A\u4E0B\u8F7D\u7C7B\u578B, \u90A3\u4E48\u8FD9\u6B21\u8BF7\u6C42\u4F1A\u88AB\u63D0\u4EA4\u5230\u6D4F\u89C8\u5668\u7684\u4E0B\u8F7D\u7BA1\u7406\u5668, \u5982\u679C\u662F HTML, \u6D4F\u89C8\u5668\u8FDB\u7A0B\u4F1A\u5411\u6E32\u67D3\u8FDB\u7A0B\u53D1\u8D77&quot;\u63D0\u4EA4\u6587\u6863&quot;\u7684\u6D88\u606F.</p><ol start="3"><li><p>chrome \u4F1A\u4E3A\u6BCF\u4E2A\u9875\u9762\u5206\u914D\u4E00\u4E2A\u6E32\u67D3\u8FDB\u7A0B. \u552F\u4E00\u7684\u4F8B\u5916\u5C31\u662F\u5F53\u4ECE A \u7F51\u7AD9\u5185\u90E8\u6253\u5F00 B \u7F51\u7AD9\u65F6, \u5982\u679C\u662F\u540C\u4E00\u7AD9\u70B9(\u5373\u534F\u8BAE\u4E0E\u6839\u57DF\u540D\u76F8\u540C), \u5219\u4F1A\u590D\u7528\u4E4B\u524D\u7684\u6E32\u67D3\u8FDB\u7A0B.</p></li><li><p>\u6E32\u67D3\u8FDB\u7A0B\u63A5\u6536\u5230&quot;\u63D0\u4EA4\u6587\u6863&quot;\u7684\u4FE1\u606F\u540E, \u4F1A\u548C\u7F51\u7EDC\u8FDB\u7A0B\u5EFA\u7ACB\u4F20\u8F93\u6570\u636E\u7684\u7BA1\u9053, \u7B49\u6587\u6863\u6570\u636E\u4F20\u8F93\u5B8C\u6210\u540E, \u6E32\u67D3\u8FDB\u7A0B\u4F1A\u8FD4\u56DE&quot;\u786E\u8BA4\u63D0\u4EA4&quot;\u7684\u4FE1\u606F\u7ED9\u6D4F\u89C8\u5668\u8FDB\u7A0B, \u6D4F\u89C8\u5668\u8FDB\u7A0B\u6536\u5230\u6D88\u606F\u540E, \u4F1A\u66F4\u65B0\u6D4F\u89C8\u5668\u754C\u9762\u72B6\u6001, \u5BFC\u822A\u6D41\u7A0B\u5C31\u7ED3\u675F\u4E86, \u8FDB\u5165\u4E0B\u4E00\u6B65\u6E32\u67D3\u9636\u6BB5.</p></li><li><p>\u6E32\u67D3\u9636\u6BB5\u7684\u5904\u7406\u6D41\u7A0B\u53EB\u505A\u6E32\u67D3\u6D41\u6C34\u7EBF, \u5305\u62EC\u4E86\u591A\u4E2A\u5B50\u9636\u6BB5.</p></li></ol><p>\u6784\u5EFA DOM \u6811</p><p>\u6837\u5F0F\u8BA1\u7B97, \u4F1A\u628A\u6240\u6709\u7684 CSS \u6837\u5F0F, \u5305\u62EC link \u4E2D\u7684\u5916\u90E8\u6587\u4EF6, style \u6807\u7B7E, \u5143\u7D20\u7684 style \u5C5E\u6027, \u8F6C\u6362\u6210 styleSheets, \u5E76\u4E14\u5BF9\u5176\u4E2D\u7684\u5C5E\u6027\u503C\u8FDB\u884C\u6807\u51C6\u5316\u64CD\u4F5C, \u7136\u540E\u8BA1\u7B97\u6BCF\u4E2A\u8282\u70B9\u7684\u6837\u5F0F, \u6839\u636E CSS \u7EE7\u627F\u548C\u5C42\u53E0\u7684\u7279\u6027, \u5C06\u6BCF\u4E2A\u8282\u70B9\u7684\u6700\u7EC8\u6837\u5F0F\u4FDD\u5B58\u5728 ComputedStyle \u7684\u7ED3\u6784\u5185.</p><p>\u5E03\u5C40, \u8BA1\u7B97\u51FA DOM \u6811\u4E2D\u53EF\u89C1\u5143\u7D20\u7684\u51E0\u4F55\u4F4D\u7F6E, \u5206\u4E3A\u4E24\u4E2A\u6B65\u9AA4, \u6784\u5EFA\u5E03\u5C40\u6811\u548C\u5E03\u5C40\u8BA1\u7B97, \u6784\u5EFA\u5E03\u5C40\u6811\u4F1A\u5FFD\u7565\u6389\u6240\u6709\u4E0D\u53EF\u89C1\u7684\u5143\u7D20.</p><p>\u5206\u5C42, \u5F53\u5143\u7D20\u6709\u4E86\u5C42\u53E0\u4E0A\u4E0B\u6587\u5C5E\u6027\u6216\u8005\u9700\u8981\u88AB\u88C1\u526A</p><h2 id="\u6D4F\u89C8\u5668\u5B89\u5168" tabindex="-1"><a class="header-anchor" href="#\u6D4F\u89C8\u5668\u5B89\u5168" aria-hidden="true">#</a> \u6D4F\u89C8\u5668\u5B89\u5168</h2><ol><li>\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\uFF08XSS\uFF09</li></ol><ul><li>\u7C7B\u578B\uFF1A\u5B58\u50A8\u578B\u3001\u53CD\u5C04\u6027\u3001DOM \u578B</li><li>\u9632\u8303\u7B56\u7565\uFF1A <ul><li>\u670D\u52A1\u5668\u5BF9\u8F93\u5165\u7684\u5185\u5BB9\u8FDB\u884C\u8FC7\u6EE4\u6216\u8F6C\u7801</li><li>\u4F7F\u7528 CSP\uFF08\u5185\u5BB9\u5B89\u5168\u7B56\u7565\uFF09\uFF1A\u901A\u8FC7 Content-Security-Policy \u5B57\u6BB5\u5F00\u542F\uFF0C\u53EF\u4EE5\u505A\u5230\u53EA\u6267\u884C\u767D\u540D\u5355\u4E2D\u7684\u811A\u672C</li><li>\u4F7F\u7528 HttpOnly \u4FDD\u62A4\u91CD\u8981\u7684 Cookie</li></ul></li></ul><ol start="2"><li>\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\uFF08CSRF\uFF09</li></ol><h2 id="xmlhttprequest" tabindex="-1"><a class="header-anchor" href="#xmlhttprequest" aria-hidden="true">#</a> XMLHttpRequest</h2><ol><li><p>\u4F7F\u7528 xhr \u5C01\u88C5\u4E00\u4E2A\u8BF7\u6C42\u51FD\u6570</p></li><li><p>\u8DE8\u57DF\u95EE\u9898</p></li></ol><ul><li>\u4EA7\u751F\u7684\u539F\u56E0: \u6D4F\u89C8\u5668\u7684\u540C\u6E90\u7B56\u7565(\u534F\u8BAE, \u57DF\u540D, \u7AEF\u53E3\u5747\u76F8\u540C\u5373\u4E3A\u540C\u6E90)</li></ul><h2 id="\u5B8F\u4EFB\u52A1\u548C\u5FAE\u4EFB\u52A1" tabindex="-1"><a class="header-anchor" href="#\u5B8F\u4EFB\u52A1\u548C\u5FAE\u4EFB\u52A1" aria-hidden="true">#</a> \u5B8F\u4EFB\u52A1\u548C\u5FAE\u4EFB\u52A1</h2><ol><li>\u5B8F\u4EFB\u52A1\u548C\u5FAE\u4EFB\u52A1\u5206\u522B\u662F\u4EC0\u4E48?</li></ol><p>\u9875\u9762\u4E2D\u7684\u5927\u90E8\u5206\u4EFB\u52A1\u90FD\u662F\u5728\u6D4F\u89C8\u5668\u4E3B\u7EBF\u7A0B\u4E2D\u6267\u884C\u7684, \u4E3A\u4E86\u8BA9\u8FD9\u4E9B\u4EFB\u52A1\u6709\u5E8F\u6267\u884C, \u5C31\u5F15\u5165\u4E86\u4E8B\u4EF6\u5FAA\u73AF\u548C\u6D88\u606F\u961F\u5217\u7684\u673A\u5236, \u6E32\u67D3\u8FDB\u7A0B\u5185\u90E8\u7EF4\u62A4\u4E00\u4E2A\u6216\u591A\u4E2A\u6D88\u606F\u961F\u5217, \u6BD4\u5982\u5EF6\u8FDF\u6D88\u606F\u961F\u5217\u548C\u666E\u901A\u6D88\u606F\u961F\u5217, \u7136\u540E\u4E3B\u7EBF\u7A0B\u4E0D\u65AD\u5730\u4ECE\u961F\u5217\u4E2D\u53D6\u51FA\u4EFB\u52A1\u5E76\u6267\u884C, \u8FD9\u4E9B\u5728\u6D88\u606F\u961F\u5217\u4E2D\u7684\u4EFB\u52A1\u5C31\u662F\u5B8F\u4EFB\u52A1.</p><p>\u6BCF\u4E2A\u5B8F\u4EFB\u52A1\u90FD\u4F1A\u6709\u4E00\u4E2A\u81EA\u5DF1\u7684\u5FAE\u4EFB\u52A1\u961F\u5217, \u6BCF\u5F53\u5B8F\u4EFB\u52A1\u6267\u884C\u7ED3\u675F\u65F6, \u4F1A\u4F9D\u6B21\u6267\u884C\u5FAE\u4EFB\u52A1\u961F\u5217\u4E2D\u7684\u4EFB\u52A1, \u5982\u679C\u5728\u6267\u884C\u5FAE\u4EFB\u52A1\u7684\u65F6\u5019\u4EA7\u751F\u4E86\u65B0\u7684\u5FAE\u4EFB\u52A1, \u4E5F\u4F1A\u7EE7\u7EED\u63A8\u5165\u8FD9\u4E2A\u961F\u5217\u4E2D, \u800C\u5FAE\u4EFB\u52A1\u7531\u4E24\u79CD\u65B9\u5F0F\u751F\u6210, \u4E00\u79CD\u662F Promise \u7684 resolve \u548C reject, \u53E6\u4E00\u79CD\u662F MutationObserver \u76D1\u63A7\u7684 DOM \u8282\u70B9\u53D1\u751F\u53D8\u5316.</p><ol start="2"><li>\u5FAE\u4EFB\u52A1\u4EA7\u751F\u7684\u539F\u56E0?</li></ol><p>\u5B8F\u4EFB\u52A1\u7684\u65F6\u95F4\u7C92\u5EA6\u6BD4\u8F83\u5927, \u4E0D\u80FD\u7CBE\u786E\u63A7\u5236\u6267\u884C\u7684\u65F6\u95F4\u95F4\u9694, \u65E0\u6CD5\u6EE1\u8DB3\u9AD8\u5B9E\u65F6\u6027\u7684\u9700\u6C42, \u6BD4\u5982\u76D1\u542C DOM \u7684\u53D8\u5316.</p><h2 id="promise" tabindex="-1"><a class="header-anchor" href="#promise" aria-hidden="true">#</a> Promise</h2><ol><li>\u5982\u4F55\u5B9E\u73B0\u4E00\u4E2A Promise?</li></ol>',32);function r(t,o){return a}var d=i(l,[["render",r]]);export{d as default};
